import { NextRequest, NextResponse } from 'next/server';
import { prisma } from '@/lib/prisma';

export async function GET(
    request: NextRequest,
    props: { params: Promise<{ id: string }> }
) {
    const params = await props.params;
    try {
        const project = await prisma.project.findUnique({
            where: { id: params.id },
            include: {
                documents: true,
                agentRuns: {
                    orderBy: { createdAt: 'desc' },
                },
                hypotheses: true,
                conceptNodes: true,
                statistics: true,
            } as any,
        });

        if (!project) {
            return NextResponse.json(
                { error: 'Project not found' },
                { status: 404 }
            );
        }

        return NextResponse.json(project);
    } catch (error) {
        console.error('GET /api/projects/[id] error:', error);
        return NextResponse.json(
            { error: 'Failed to fetch project' },
            { status: 500 }
        );
    }
}

export async function DELETE(
    request: NextRequest,
    props: { params: Promise<{ id: string }> }
) {
    const params = await props.params;
    try {
        await prisma.project.delete({
            where: { id: params.id },
        });

        return NextResponse.json({ success: true });
    } catch (error) {
        console.error('DELETE /api/projects/[id] error:', error);
        return NextResponse.json(
            { error: 'Failed to delete project' },
            { status: 500 }
        );
    }
}

'use client';

import { useState, useEffect } from 'react';
import { Sidebar } from '@/components/sidebar';
import { ProjectCard } from '@/components/project-card';
import { AgentPipeline } from '@/components/agent-pipeline';
import { QualityMetricsChart } from '@/components/quality-metrics-chart';
import { CitationChart } from '@/components/citation-chart';
import { WordCountTrend } from '@/components/word-count-trend';
import { ConceptNetwork } from '@/components/concept-network';
import { HypothesisTable } from '@/components/hypothesis-table';
import { DocumentTypeChart } from '@/components/document-type-chart';
import { FileUpload } from '@/components/file-upload';
import { NewProjectDialog } from '@/components/new-project-dialog';
import { ProjectDetailsView } from '@/components/project-details-view';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { useProjects } from '@/hooks/use-projects';
import { useAnalytics } from '@/hooks/use-analytics';
import {
  mockAgents,
  mockQualityMetrics,
  mockCitationData,
  mockWordCountTrend,
  mockConceptNodes,
  mockHypotheses
} from '@/lib/mock-data';
import {
  Sparkles,
  FolderOpen,
  BarChart3,
  Settings as SettingsIcon,
  Search,
  Filter,
  Download,
  Upload,
  Trash2,
  Archive,
  Bell,
  User,
  Lock,
  Palette,
  Globe,
  Zap,
  TrendingUp,
  Clock,
  CheckCircle2,
  AlertCircle,
  Loader2,
  RefreshCw,
  FileText
} from 'lucide-react';

type TabType = 'dashboard' | 'projects' | 'analytics' | 'settings';

export default function Home() {
  const [activeTab, setActiveTab] = useState<TabType>('dashboard');
  const [isNewProjectOpen, setIsNewProjectOpen] = useState(false);
  const { projects, loading, error, refetch } = useProjects();
  const [selectedProjectId, setSelectedProjectId] = useState<string | null>(null);
  const [selectedProjectData, setSelectedProjectData] = useState<any>(null);
  const [dashboardView, setDashboardView] = useState<'list' | 'details'>('list');

  // Fetch selected project details with polling
  useEffect(() => {
    if (!selectedProjectId) return;

    const fetchDetails = () => {
      fetch(`/api/projects/${selectedProjectId}`)
        .then(res => res.json())
        .then(data => setSelectedProjectData(data))
        .catch(console.error);
    };

    fetchDetails();

    // Poll only if processing
    const interval = setInterval(() => {
      if (selectedProjectData?.status === 'processing') {
        fetchDetails();
      }
    }, 3000);

    return () => clearInterval(interval);
  }, [selectedProjectId, selectedProjectData?.status]);

  // Auto-select first project - REMOVED to show list first
  // useEffect(() => {
  //   if (projects.length > 0 && !selectedProjectId) {
  //     setSelectedProjectId(projects[0].id);
  //   }
  // }, [projects, selectedProjectId]);

  const handleProjectClick = (projectId: string) => {
    setSelectedProjectId(projectId);
    setDashboardView('details');
  };

  const handleBackToDashboard = () => {
    setDashboardView('list');
    setSelectedProjectId(null);
    setSelectedProjectData(null);
  };

  // Dashboard Content
  const DashboardContent = () => (
    <div className="space-y-8">
      {/* Dashboard Views */}
      {dashboardView === 'list' ? (
        <>
          {/* Header with Gradient */}
          <div className="relative overflow-hidden rounded-2xl bg-gradient-to-br from-primary/10 via-secondary/10 to-chart-3/10 p-8 border animate-fadeIn">
            <div className="absolute top-0 right-0 w-64 h-64 bg-primary/5 rounded-full blur-3xl" />
            <div className="absolute bottom-0 left-0 w-64 h-64 bg-secondary/5 rounded-full blur-3xl" />
            <div className="relative z-10">
              <div className="flex items-center justify-between">
                <div className="flex items-center gap-3 mb-2">
                  <Sparkles className="w-8 h-8 text-primary" />
                  <h1 className="text-4xl font-serif font-bold text-foreground">
                    Dashboard
                  </h1>
                </div>
                <Button variant="outline" size="sm" onClick={refetch} className="gap-2">
                  <RefreshCw className="w-4 h-4" />
                  Refresh
                </Button>
              </div>
              <p className="text-muted-foreground text-lg">
                Manage your AI-powered research paper generation projects
              </p>
            </div>
          </div>

          {/* Projects Grid */}
          <section className="animate-fadeIn" style={{ animationDelay: '0.2s' }}>
            <h2 className="text-2xl font-serif font-semibold mb-6 flex items-center gap-2">
              <span className="w-1 h-8 bg-primary rounded-full" />
              Your Projects
            </h2>

            {loading && (
              <div className="flex items-center justify-center py-12">
                <Loader2 className="w-8 h-8 animate-spin text-primary" />
                <span className="ml-3 text-muted-foreground">Loading projects...</span>
              </div>
            )}

            {error && (
              <Card className="border-destructive/50 bg-destructive/5">
                <CardContent className="p-6">
                  <div className="flex items-center gap-3">
                    <AlertCircle className="w-5 h-5 text-destructive" />
                    <div>
                      <p className="font-semibold text-destructive">Error loading projects</p>
                      <p className="text-sm text-muted-foreground">{error}</p>
                    </div>
                  </div>
                </CardContent>
              </Card>
            )}

            {!loading && !error && projects.length === 0 && (
              <Card>
                <CardContent className="p-12 text-center">
                  <FolderOpen className="w-16 h-16 mx-auto mb-4 text-muted-foreground" />
                  <h3 className="text-xl font-semibold mb-2">No projects yet</h3>
                  <p className="text-muted-foreground mb-4">
                    Create your first project to get started with AI research paper generation
                  </p>
                  <Button onClick={() => setIsNewProjectOpen(true)}>
                    <Upload className="w-4 h-4 mr-2" />
                    Create Project
                  </Button>
                </CardContent>
              </Card>
            )}

            {!loading && !error && projects.length > 0 && (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {projects.map((project, index) => {
                  const displayProject = {
                    id: project.id,
                    title: project.name,
                    description: project.description || '',
                    status: project.status as any,
                    lastUpdated: new Date(project.updatedAt),
                    documentCount: project._count?.documents || 0,
                    progress: project.progress,
                  };

                  return (
                    <div key={project.id} style={{ animationDelay: `${0.3 + index * 0.1}s` }}>
                      <ProjectCard
                        project={displayProject}
                        onClick={() => handleProjectClick(project.id)}
                      />
                    </div>
                  );
                })}
              </div>
            )}
          </section>
        </>
      ) : (
        <ProjectDetailsView
          project={selectedProjectData}
          onBack={handleBackToDashboard}
          onRefresh={() => {
            const fetchDetails = () => {
              fetch(`/api/projects/${selectedProjectId}`)
                .then(res => res.json())
                .then(data => setSelectedProjectData(data))
                .catch(console.error);
            };
            fetchDetails();
          }}
        />
      )}
    </div>
  );

  // Projects Content
  const ProjectsContent = () => (
    <div className="space-y-6 animate-fadeIn">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-4xl font-serif font-bold mb-2 flex items-center gap-3">
            <FolderOpen className="w-10 h-10 text-primary" />
            Projects
          </h1>
          <p className="text-muted-foreground">Manage all your research paper projects</p>
        </div>
        <Button className="gap-2">
          <Upload className="w-4 h-4" />
          New Project
        </Button>
      </div>

      {/* Search and Filter */}
      <Card>
        <CardContent className="p-4">
          <div className="flex gap-4">
            <div className="flex-1 relative">
              <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground" />
              <input
                type="text"
                placeholder="Search projects..."
                className="w-full pl-10 pr-4 py-2 bg-background border rounded-lg focus:outline-none focus:ring-2 focus:ring-ring"
              />
            </div>
            <Button variant="outline" className="gap-2">
              <Filter className="w-4 h-4" />
              Filter
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Projects List */}
      <div className="grid grid-cols-1 gap-4">
        {loading && (
          <div className="flex items-center justify-center py-12">
            <Loader2 className="w-8 h-8 animate-spin text-primary" />
            <span className="ml-3 text-muted-foreground">Loading projects...</span>
          </div>
        )}

        {error && (
          <Card className="border-destructive/50 bg-destructive/5">
            <CardContent className="p-6">
              <p className="text-destructive">Error loading projects: {error}</p>
            </CardContent>
          </Card>
        )}

        {!loading && !error && projects.length === 0 && (
          <Card>
            <CardContent className="p-12 text-center">
              <p className="text-muted-foreground mb-4">No projects found.</p>
              <Button onClick={() => setIsNewProjectOpen(true)}>
                Create Project
              </Button>
            </CardContent>
          </Card>
        )}

        {!loading && !error && projects.map((project, index) => {
          const displayProject = {
            id: project.id,
            title: project.name,
            description: project.description || '',
            status: project.status as any,
            lastUpdated: new Date(project.updatedAt),
            documentCount: project._count?.documents || 0,
            progress: project.progress,
          };

          return (
            <Card key={project.id} className="hover-lift transition-smooth animate-slideIn" style={{ animationDelay: `${index * 0.1}s` }}>
              <CardContent className="p-6">
                <div className="flex items-start justify-between gap-4">
                  <div className="flex-1">
                    <div className="flex items-center gap-3 mb-2">
                      <h3 className="text-xl font-semibold">{displayProject.title}</h3>
                      <Badge variant={displayProject.status === 'completed' ? 'default' : displayProject.status === 'processing' ? 'secondary' : 'outline'}>
                        {displayProject.status}
                      </Badge>
                    </div>
                    <p className="text-muted-foreground mb-4">{displayProject.description}</p>

                    <div className="grid grid-cols-3 gap-4 mb-4">
                      <div className="flex items-center gap-2 text-sm">
                        <Clock className="w-4 h-4 text-muted-foreground" />
                        <span className="font-mono">{displayProject.lastUpdated.toLocaleDateString()}</span>
                      </div>
                      <div className="flex items-center gap-2 text-sm">
                        <FolderOpen className="w-4 h-4 text-muted-foreground" />
                        <span className="font-mono">{displayProject.documentCount} documents</span>
                      </div>
                      <div className="flex items-center gap-2 text-sm">
                        <TrendingUp className="w-4 h-4 text-muted-foreground" />
                        <span className="font-mono">{displayProject.progress}% complete</span>
                      </div>
                    </div>

                    <Progress value={displayProject.progress} className="h-2" />
                  </div>

                  <div className="flex gap-2">
                    <Button variant="outline" size="sm">
                      <Download className="w-4 h-4" />
                    </Button>
                    <Button variant="outline" size="sm">
                      <Archive className="w-4 h-4" />
                    </Button>
                    <Button variant="outline" size="sm" onClick={() => {
                      if (confirm('Are you sure you want to delete this project?')) {
                        fetch(`/api/projects/${project.id}`, { method: 'DELETE' })
                          .then(() => refetch());
                      }
                    }}>
                      <Trash2 className="w-4 h-4" />
                    </Button>
                  </div>
                </div>
              </CardContent>
            </Card>
          )
        })}
      </div>
    </div>
  );

  // Analytics Content
  const AnalyticsContent = () => {
    const { data: analytics, loading: analyticsLoading, error: analyticsError } = useAnalytics();

    if (analyticsLoading) {
      return (
        <div className="flex items-center justify-center h-96">
          <div className="flex flex-col items-center gap-4">
            <Loader2 className="w-8 h-8 text-primary animate-spin" />
            <p className="text-muted-foreground">Loading analytics...</p>
          </div>
        </div>
      );
    }

    if (analyticsError || !analytics) {
      return (
        <div className="flex items-center justify-center h-96">
          <div className="flex flex-col items-center gap-4">
            <AlertCircle className="w-8 h-8 text-destructive" />
            <p className="text-destructive">Failed to load analytics</p>
            <Button variant="outline" onClick={() => window.location.reload()}>Retry</Button>
          </div>
        </div>
      );
    }

    return (
      <div className="space-y-6 animate-fadeIn">
        {/* Header */}
        <div>
          <h1 className="text-4xl font-serif font-bold mb-2 flex items-center gap-3">
            <BarChart3 className="w-10 h-10 text-primary" />
            Analytics
          </h1>
          <p className="text-muted-foreground">Comprehensive insights across all projects</p>
        </div>

        {/* Stats Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          <Card className="animate-scaleIn">
            <CardHeader className="pb-3">
              <CardDescription>Total Projects</CardDescription>
              <CardTitle className="text-4xl font-mono">{analytics.totalProjects}</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="flex items-center gap-2 text-sm text-muted-foreground">
                <FolderOpen className="w-4 h-4 text-primary" />
                <span>Active research</span>
              </div>
            </CardContent>
          </Card>

          <Card className="animate-scaleIn" style={{ animationDelay: '0.1s' }}>
            <CardHeader className="pb-3">
              <CardDescription>Total Documents</CardDescription>
              <CardTitle className="text-4xl font-mono">{analytics.totalDocuments}</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="flex items-center gap-2 text-sm text-muted-foreground">
                <FileText className="w-4 h-4 text-chart-2" />
                <span>Processed files</span>
              </div>
            </CardContent>
          </Card>

          <Card className="animate-scaleIn" style={{ animationDelay: '0.2s' }}>
            <CardHeader className="pb-3">
              <CardDescription>Avg Quality Score</CardDescription>
              <CardTitle className="text-4xl font-mono">{analytics.avgQuality}%</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="flex items-center gap-2 text-sm text-muted-foreground">
                <Sparkles className="w-4 h-4 text-chart-4" />
                <span>Based on hypotheses</span>
              </div>
            </CardContent>
          </Card>

          <Card className="animate-scaleIn" style={{ animationDelay: '0.3s' }}>
            <CardHeader className="pb-3">
              <CardDescription>Research Velocity</CardDescription>
              <CardTitle className="text-4xl font-mono">High</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="flex items-center gap-2 text-sm text-muted-foreground">
                <Clock className="w-4 h-4 text-secondary" />
                <span>Avg per paper</span>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Charts */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <CitationChart data={analytics.citationData} />
          <WordCountTrend data={analytics.wordCountTrend} />
          <QualityMetricsChart metrics={analytics.qualityMetrics} />
          <DocumentTypeChart />
          <ConceptNetwork nodes={analytics.conceptNodes} />
        </div>
      </div>
    );
  };

  // Settings Content
  const SettingsContent = () => (
    <div className="space-y-6 animate-fadeIn max-w-4xl">
      {/* Header */}
      <div>
        <h1 className="text-4xl font-serif font-bold mb-2 flex items-center gap-3">
          <SettingsIcon className="w-10 h-10 text-primary" />
          Settings
        </h1>
        <p className="text-muted-foreground">Customize your research assistant</p>
      </div>

      {/* Profile Settings */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <User className="w-5 h-5" />
            Profile
          </CardTitle>
          <CardDescription>Manage your account information</CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="text-sm font-medium mb-2 block">Full Name</label>
              <input type="text" placeholder="John Doe" className="w-full px-3 py-2 bg-background border rounded-lg" />
            </div>
            <div>
              <label className="text-sm font-medium mb-2 block">Email</label>
              <input type="email" placeholder="john@example.com" className="w-full px-3 py-2 bg-background border rounded-lg" />
            </div>
          </div>
          <Button>Save Changes</Button>
        </CardContent>
      </Card>

      {/* Appearance */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Palette className="w-5 h-5" />
            Appearance
          </CardTitle>
          <CardDescription>Customize the look and feel</CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div>
            <label className="text-sm font-medium mb-2 block">Theme</label>
            <div className="flex gap-4">
              <Button variant="outline" className="flex-1">Light</Button>
              <Button variant="outline" className="flex-1">Dark</Button>
              <Button variant="outline" className="flex-1">System</Button>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Notifications */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Bell className="w-5 h-5" />
            Notifications
          </CardTitle>
          <CardDescription>Configure notification preferences</CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          {['Project completion', 'Agent errors', 'Weekly summary'].map((item) => (
            <div key={item} className="flex items-center justify-between">
              <span className="text-sm">{item}</span>
              <input type="checkbox" className="w-4 h-4" defaultChecked />
            </div>
          ))}
        </CardContent>
      </Card>

      {/* API Settings */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Globe className="w-5 h-5" />
            API Configuration
          </CardTitle>
          <CardDescription>Configure AI model settings</CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div>
            <label className="text-sm font-medium mb-2 block">API Provider</label>
            <select className="w-full px-3 py-2 bg-background border rounded-lg">
              <option>Gemini API</option>
              <option>OpenAI</option>
              <option>Anthropic</option>
            </select>
          </div>
          <div>
            <label className="text-sm font-medium mb-2 block">API Key</label>
            <input type="password" placeholder="ΓÇóΓÇóΓÇóΓÇóΓÇóΓÇóΓÇóΓÇóΓÇóΓÇóΓÇóΓÇóΓÇóΓÇóΓÇóΓÇó" className="w-full px-3 py-2 bg-background border rounded-lg" />
          </div>
          <Button>Update API Settings</Button>
        </CardContent>
      </Card>

      {/* Security */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Lock className="w-5 h-5" />
            Security
          </CardTitle>
          <CardDescription>Manage security settings</CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <Button variant="outline" className="w-full justify-start">
            Change Password
          </Button>
          <Button variant="outline" className="w-full justify-start">
            Two-Factor Authentication
          </Button>
          <Button variant="destructive" className="w-full justify-start">
            Delete Account
          </Button>
        </CardContent>
      </Card>
    </div>
  );

  return (
    <div className="flex h-screen bg-background overflow-hidden">
      <Sidebar
        onNewProject={() => setIsNewProjectOpen(true)}
        activeTab={activeTab}
        onTabChange={setActiveTab}
      />

      <main className="flex-1 overflow-auto">
        <div className="p-8">
          {activeTab === 'dashboard' && <DashboardContent />}
          {activeTab === 'projects' && <ProjectsContent />}
          {activeTab === 'analytics' && <AnalyticsContent />}
          {activeTab === 'settings' && <SettingsContent />}

          <div className="h-8" />
        </div>
      </main>

      {/* New Project Dialog */}
      <NewProjectDialog
        open={isNewProjectOpen}
        onOpenChange={setIsNewProjectOpen}
      />
    </div>
  );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Agent } from '@/lib/types';
import { Clock, CheckCircle2, Loader2, XCircle, Circle, ChevronDown, ChevronUp, Terminal, Activity } from 'lucide-react';
import { cn } from '@/lib/utils';

interface AgentPipelineProps {
    status?: string;
    progress?: number;
    agentRuns?: any[];
    agents?: Agent[]; // Fallback for mock data
}

const DEFAULT_AGENTS: Agent[] = [
    { id: 'reader', name: 'Reader Agent', status: 'pending' },
    { id: 'summarizer', name: 'Summarizer Agent', status: 'pending' },
    { id: 'graph', name: 'Graph Agent', status: 'pending' },
    { id: 'hypothesis', name: 'Hypothesis Agent', status: 'pending' },
    { id: 'experiment', name: 'Experiment Agent', status: 'pending' },
    { id: 'writer', name: 'Paper Writer Agent', status: 'pending' },
];

export function AgentPipeline({ status, progress, agentRuns, agents: propAgents }: AgentPipelineProps) {
    const [expandedAgent, setExpandedAgent] = useState<string | null>(null);

    // Merge backend data with default agents
    const displayAgents: Agent[] = agentRuns ? DEFAULT_AGENTS.map(defaultAgent => {
        const run = agentRuns.find(r => r.agentName === defaultAgent.id);
        if (run) {
            return {
                ...defaultAgent,
                status: run.status as Agent['status'],
                output: run.output ? JSON.parse(run.output) : null,
                executionTime: run.executionTime,
                error: run.error
            };
        }
        return defaultAgent;
    }) : (propAgents || DEFAULT_AGENTS);

    const getStatusIcon = (status: string) => {
        switch (status) {
            case 'completed':
                return <CheckCircle2 className="w-5 h-5 text-primary" />;
            case 'running':
            case 'processing':
                return <Loader2 className="w-5 h-5 text-chart-3 animate-spin" />;
            case 'error':
                return <XCircle className="w-5 h-5 text-destructive" />;
            default:
                return <Circle className="w-5 h-5 text-muted-foreground/30" />;
        }
    };

    const getStatusBadge = (status: string) => {
        switch (status) {
            case 'completed':
                return <Badge variant="default" className="bg-primary/20 text-primary hover:bg-primary/30 border-primary/20">Completed</Badge>;
            case 'running':
            case 'processing':
                return (
                    <Badge variant="secondary" className="bg-chart-3/20 text-chart-3 hover:bg-chart-3/30 border-chart-3/20 animate-pulse gap-1">
                        <Activity className="w-3 h-3 animate-bounce" /> Processing
                    </Badge>
                );
            case 'error':
                return <Badge variant="destructive">Error</Badge>;
            default:
                return <Badge variant="outline" className="text-muted-foreground">Pending</Badge>;
        }
    };

    const toggleExpand = (agentId: string) => {
        setExpandedAgent(expandedAgent === agentId ? null : agentId);
    };

    return (
        <Card className="shadow-premium animate-fadeIn overflow-hidden border-muted/40">
            <CardHeader className="bg-muted/20 pb-4">
                <div className="flex items-center justify-between">
                    <div>
                        <CardTitle className="flex items-center gap-2">
                            <Terminal className="w-5 h-5 text-primary" />
                            Agent Pipeline
                        </CardTitle>
                        <CardDescription>Real-time multi-agent workflow execution</CardDescription>
                    </div>
                    {/* Overall Progress */}
                    <div className="flex items-center gap-2">
                        <div className="text-right mr-2">
                            <p className="text-xs font-medium text-muted-foreground">Total Progress</p>
                            <p className="text-lg font-bold font-mono text-primary">
                                {Math.round(progress || (displayAgents.filter((a: any) => a.status === 'completed').length / displayAgents.length) * 100)}%
                            </p>
                        </div>
                        <div className="h-10 w-1 bg-muted rounded-full overflow-hidden">
                            <div
                                className="w-full bg-primary transition-all duration-500 ease-out"
                                style={{ height: `${progress || (displayAgents.filter((a: any) => a.status === 'completed').length / displayAgents.length) * 100}%` }}
                            />
                        </div>
                    </div>
                </div>
            </CardHeader>
            <CardContent className="p-0">
                <div className="divide-y divide-border/50">
                    {displayAgents.map((agent, index) => {
                        const isExpanded = expandedAgent === agent.id;
                        const hasOutput = agent.output || agent.status === 'completed';
                        const isRunning = agent.status === 'running' || agent.status === 'processing';

                        return (
                            <div key={agent.id} className={cn(
                                "transition-all duration-300",
                                isRunning ? "bg-primary/5" : "hover:bg-muted/20"
                            )}>
                                <div
                                    className="p-4 flex items-center gap-4 cursor-pointer"
                                    onClick={() => hasOutput && toggleExpand(agent.id)}
                                >
                                    {/* Status Icon with Connector Line */}
                                    <div className="relative flex flex-col items-center">
                                        {index > 0 && (
                                            <div className={cn(
                                                "absolute -top-8 w-0.5 h-8 -z-10",
                                                displayAgents[index - 1].status === 'completed' ? "bg-primary/50" : "bg-muted"
                                            )} />
                                        )}
                                        <div className={cn(
                                            "relative z-10 rounded-full p-1 bg-background border-2 transition-colors duration-300",
                                            agent.status === 'completed' ? "border-primary" :
                                                isRunning ? "border-chart-3 shadow-[0_0_10px_rgba(0,0,0,0.1)] shadow-chart-3/30" : "border-muted"
                                        )}>
                                            {getStatusIcon(agent.status)}
                                        </div>
                                    </div>

                                    {/* Agent Info */}
                                    <div className="flex-1 min-w-0">
                                        <div className="flex items-center justify-between mb-1">
                                            <h4 className={cn(
                                                "font-medium truncate",
                                                agent.status === 'completed' ? "text-foreground" : "text-muted-foreground"
                                            )}>
                                                {agent.name}
                                            </h4>
                                            {getStatusBadge(agent.status)}
                                        </div>

                                        <div className="flex items-center gap-4 text-xs text-muted-foreground">
                                            {agent.executionTime && (
                                                <span className="flex items-center gap-1 font-mono">
                                                    <Clock className="w-3 h-3" />
                                                    {agent.executionTime}s
                                                </span>
                                            )}
                                            {hasOutput && (
                                                <span className="text-primary/80 group-hover:text-primary transition-colors">
                                                    {isExpanded ? 'Click to collapse logs' : 'Click to view logs'}
                                                </span>
                                            )}
                                        </div>
                                    </div>

                                    {hasOutput && (
                                        <div className="text-muted-foreground">
                                            {isExpanded ? <ChevronUp className="w-4 h-4" /> : <ChevronDown className="w-4 h-4" />}
                                        </div>
                                    )}
                                </div>

                                {/* Expanded Logs */}
                                <div className={cn(
                                    "grid transition-all duration-300 ease-in-out bg-black/5 dark:bg-black/20",
                                    isExpanded ? "grid-rows-[1fr] opacity-100" : "grid-rows-[0fr] opacity-0"
                                )}>
                                    <div className="overflow-hidden">
                                        <div className="p-4 pt-0">
                                            <div className="mt-2 rounded-md border bg-card p-4 font-mono text-xs shadow-inner max-h-[300px] overflow-y-auto custom-scrollbar">
                                                <div className="flex items-center gap-2 mb-2 text-muted-foreground border-b pb-2">
                                                    <Terminal className="w-3 h-3" />
                                                    <span>Agent Output Log</span>
                                                </div>
                                                <pre className="whitespace-pre-wrap text-foreground/90 leading-relaxed">
                                                    {typeof agent.output === 'string'
                                                        ? agent.output
                                                        : JSON.stringify(agent.output, null, 2)}
                                                </pre>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        );
                    })}
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';

interface ConceptNode {
    id: string;
    label: string;
    importance: number;
    cluster: number;
}

interface ConceptNetworkProps {
    nodes: ConceptNode[];
}

export function ConceptNetwork({ nodes }: ConceptNetworkProps) {
    const clusters = Array.from(new Set(nodes.map(n => n.cluster)));
    const clusterColors = [
        'bg-chart-1',
        'bg-chart-2',
        'bg-chart-3',
        'bg-primary',
        'bg-secondary',
    ];

    const getNodeSize = (importance: number) => {
        if (importance > 80) return 'w-16 h-16 text-sm';
        if (importance > 60) return 'w-14 h-14 text-xs';
        if (importance > 40) return 'w-12 h-12 text-xs';
        return 'w-10 h-10 text-xs';
    };

    return (
        <Card className="shadow-premium animate-fadeIn">
            <CardHeader>
                <CardTitle>Concept Network</CardTitle>
                <CardDescription>Research concept relationships and clusters</CardDescription>
            </CardHeader>
            <CardContent>
                {/* Legend */}
                <div className="flex flex-wrap gap-2 mb-6">
                    {clusters.map((cluster, idx) => (
                        <Badge key={cluster} variant="outline" className="gap-2">
                            <div className={`w-3 h-3 rounded-full ${clusterColors[idx % clusterColors.length]}`} />
                            Cluster {cluster}
                        </Badge>
                    ))}
                </div>

                {/* Network Visualization */}
                <div className="relative h-96 bg-muted/20 rounded-lg border overflow-hidden">
                    <div className="absolute inset-0 flex items-center justify-center p-8">
                        <div className="relative w-full h-full">
                            {nodes.map((node, index) => {
                                // Circular layout
                                const angle = (index / nodes.length) * 2 * Math.PI;
                                const radius = 35; // percentage
                                const x = 50 + radius * Math.cos(angle);
                                const y = 50 + radius * Math.sin(angle);

                                return (
                                    <div
                                        key={node.id}
                                        className="absolute transform -translate-x-1/2 -translate-y-1/2 animate-scaleIn"
                                        style={{
                                            left: `${x}%`,
                                            top: `${y}%`,
                                            animationDelay: `${index * 0.05}s`,
                                        }}
                                    >
                                        <div
                                            className={`
                        ${getNodeSize(node.importance)}
                        ${clusterColors[node.cluster % clusterColors.length]}
                        rounded-full flex items-center justify-center
                        font-semibold text-white shadow-lg
                        hover:scale-110 transition-transform cursor-pointer
                        hover:shadow-xl
                      `}
                                            title={`${node.label} (Importance: ${node.importance})`}
                                        >
                                            <span className="text-center px-1 truncate">
                                                {node.label.split(' ')[0]}
                                            </span>
                                        </div>
                                    </div>
                                );
                            })}

                            {/* Connection lines (simplified) */}
                            <svg className="absolute inset-0 w-full h-full pointer-events-none opacity-20">
                                {nodes.map((node, i) => {
                                    const nextNode = nodes[(i + 1) % nodes.length];
                                    const angle1 = (i / nodes.length) * 2 * Math.PI;
                                    const angle2 = ((i + 1) / nodes.length) * 2 * Math.PI;
                                    const radius = 35;

                                    const x1 = 50 + radius * Math.cos(angle1);
                                    const y1 = 50 + radius * Math.sin(angle1);
                                    const x2 = 50 + radius * Math.cos(angle2);
                                    const y2 = 50 + radius * Math.sin(angle2);

                                    return (
                                        <line
                                            key={`line-${i}`}
                                            x1={`${x1}%`}
                                            y1={`${y1}%`}
                                            x2={`${x2}%`}
                                            y2={`${y2}%`}
                                            stroke="currentColor"
                                            strokeWidth="1"
                                            className="text-border"
                                        />
                                    );
                                })}
                            </svg>
                        </div>
                    </div>

                    {/* Center label */}
                    <div className="absolute inset-0 flex items-center justify-center pointer-events-none">
                        <div className="bg-background/80 backdrop-blur-sm rounded-full w-24 h-24 flex items-center justify-center border-2 border-primary/20">
                            <span className="text-xs font-semibold text-center text-muted-foreground">
                                Research<br />Concepts
                            </span>
                        </div>
                    </div>
                </div>

                {/* Stats */}
                <div className="grid grid-cols-3 gap-4 mt-6">
                    <div className="text-center p-3 bg-muted/30 rounded-lg">
                        <p className="text-2xl font-bold font-mono text-primary">{nodes.length}</p>
                        <p className="text-xs text-muted-foreground mt-1">Concepts</p>
                    </div>
                    <div className="text-center p-3 bg-muted/30 rounded-lg">
                        <p className="text-2xl font-bold font-mono text-chart-2">{clusters.length}</p>
                        <p className="text-xs text-muted-foreground mt-1">Clusters</p>
                    </div>
                    <div className="text-center p-3 bg-muted/30 rounded-lg">
                        <p className="text-2xl font-bold font-mono text-chart-3">
                            {Math.round(nodes.reduce((sum, n) => sum + n.importance, 0) / nodes.length)}
                        </p>
                        <p className="text-xs text-muted-foreground mt-1">Avg Importance</p>
                    </div>
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import React from 'react';
import 'katex/dist/katex.min.css';
import { InlineMath, BlockMath } from 'react-katex';

interface MathRendererProps {
    content: string;
    className?: string;
}

/**
 * Renders text with LaTeX mathematical formulas
 * Supports inline math: $formula$ and block math: $$formula$$
 */
export const MathRenderer: React.FC<MathRendererProps> = ({ content, className = '' }) => {
    const renderContent = () => {
        const parts: React.ReactNode[] = [];
        let currentIndex = 0;
        let key = 0;

        // Regular expressions for block and inline math
        const blockMathRegex = /\$\$(.*?)\$\$/gs;
        const inlineMathRegex = /\$(.*?)\$/g;

        // First, find all block math sections
        const blockMatches: Array<{ start: number; end: number; formula: string }> = [];
        let match;

        while ((match = blockMathRegex.exec(content)) !== null) {
            blockMatches.push({
                start: match.index,
                end: match.index + match[0].length,
                formula: match[1],
            });
        }

        // Process content
        let lastEnd = 0;
        const segments: Array<{ type: 'text' | 'block' | 'inline'; content: string; start: number; end: number }> = [];

        // Add block math segments
        blockMatches.forEach((blockMatch) => {
            if (blockMatch.start > lastEnd) {
                segments.push({
                    type: 'text',
                    content: content.substring(lastEnd, blockMatch.start),
                    start: lastEnd,
                    end: blockMatch.start,
                });
            }
            segments.push({
                type: 'block',
                content: blockMatch.formula,
                start: blockMatch.start,
                end: blockMatch.end,
            });
            lastEnd = blockMatch.end;
        });

        // Add remaining text
        if (lastEnd < content.length) {
            segments.push({
                type: 'text',
                content: content.substring(lastEnd),
                start: lastEnd,
                end: content.length,
            });
        }

        // Process text segments for inline math
        const processedSegments: Array<{ type: 'text' | 'block' | 'inline'; content: string }> = [];
        segments.forEach((segment) => {
            if (segment.type === 'text') {
                let textContent = segment.content;
                let lastIdx = 0;
                let inlineMatch;
                const inlineRegex = /\$(.*?)\$/g;

                while ((inlineMatch = inlineRegex.exec(textContent)) !== null) {
                    if (inlineMatch.index > lastIdx) {
                        processedSegments.push({
                            type: 'text',
                            content: textContent.substring(lastIdx, inlineMatch.index),
                        });
                    }
                    processedSegments.push({
                        type: 'inline',
                        content: inlineMatch[1],
                    });
                    lastIdx = inlineMatch.index + inlineMatch[0].length;
                }

                if (lastIdx < textContent.length) {
                    processedSegments.push({
                        type: 'text',
                        content: textContent.substring(lastIdx),
                    });
                }
            } else {
                processedSegments.push(segment);
            }
        });

        // Render segments
        return processedSegments.map((segment, idx) => {
            if (segment.type === 'block') {
                return (
                    <div key={idx} className="my-4">
                        <BlockMath math={segment.content} />
                    </div>
                );
            } else if (segment.type === 'inline') {
                return <InlineMath key={idx} math={segment.content} />;
            } else {
                return <span key={idx}>{segment.content}</span>;
            }
        });
    };

    return <div className={className}>{renderContent()}</div>;
};

/**
 * Simple wrapper for rendering a single formula
 */
export const Formula: React.FC<{ formula: string; block?: boolean }> = ({ formula, block = false }) => {
    if (block) {
        return (
            <div className="my-4">
                <BlockMath math={formula} />
            </div>
        );
    }
    return <InlineMath math={formula} />;
};

'use client';

import { useState, useEffect, useRef } from 'react';
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Upload, FileText, CheckCircle2, Loader2, X, FileType } from 'lucide-react';
import { cn } from '@/lib/utils';
import { useToast } from '@/components/ui/toast';

interface NewProjectDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  onProjectCreated?: () => void;
}

const AGENT_STEPS = [
  { id: 'reader', label: 'Reader Agent', description: 'Parsing documents' },
  { id: 'summarizer', label: 'Summarizer Agent', description: 'Extracting key insights' },
  { id: 'graph', label: 'Graph Agent', description: 'Building concept network' },
  { id: 'hypothesis', label: 'Hypothesis Agent', description: 'Generating hypotheses' },
  { id: 'experiment', label: 'Experiment Agent', description: 'Designing experiments' },
  { id: 'writer', label: 'Paper Writer Agent', description: 'Drafting content' },
];

export function NewProjectDialog({ open, onOpenChange, onProjectCreated }: NewProjectDialogProps) {
  const [projectName, setProjectName] = useState('');
  const [description, setDescription] = useState('');
  const [files, setFiles] = useState<File[]>([]);
  const [isCreating, setIsCreating] = useState(false);
  const [dragActive, setDragActive] = useState(false);
  const [currentStep, setCurrentStep] = useState<number>(-1);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const { addToast } = useToast();

  // Reset state when dialog opens/closes
  useEffect(() => {
    if (!open) {
      setProjectName('');
      setDescription('');
      setFiles([]);
      setIsCreating(false);
      setCurrentStep(-1);
      setDragActive(false);
    }
  }, [open]);

  // Simulate agent initialization progress
  useEffect(() => {
    if (isCreating) {
      const interval = setInterval(() => {
        setCurrentStep(prev => {
          if (prev < AGENT_STEPS.length - 1) return prev + 1;
          return prev;
        });
      }, 800);
      return () => clearInterval(interval);
    }
  }, [isCreating]);

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files) {
      addFiles(Array.from(e.target.files));
    }
  };

  const addFiles = (newFiles: File[]) => {
    const validFiles = newFiles.filter(file =>
      file.type === 'application/pdf' ||
      file.type === 'application/vnd.openxmlformats-officedocument.wordprocessingml.document' ||
      file.name.endsWith('.pdf') ||
      file.name.endsWith('.docx')
    );

    if (validFiles.length !== newFiles.length) {
      addToast('Some files were skipped. Only PDF and DOCX files are allowed.', 'warning');
    }

    setFiles(prev => [...prev, ...validFiles]);
  };

  const handleDrag = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === 'dragenter' || e.type === 'dragover') {
      setDragActive(true);
    } else if (e.type === 'dragleave') {
      setDragActive(false);
    }
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    if (e.dataTransfer.files && e.dataTransfer.files.length > 0) {
      addFiles(Array.from(e.dataTransfer.files));
    }
  };

  const removeFile = (index: number) => {
    setFiles(files.filter((_, i) => i !== index));
  };

  const handleCreate = async () => {
    if (!projectName.trim() || files.length === 0) return;

    setIsCreating(true);
    setCurrentStep(0);

    try {
      // Create project
      const projectRes = await fetch('/api/projects', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name: projectName, description }),
      });

      if (!projectRes.ok) throw new Error('Failed to create project');

      const project = await projectRes.json();

      // Upload files
      for (const file of files) {
        const formData = new FormData();
        formData.append('file', file);
        formData.append('projectId', project.id);

        const uploadRes = await fetch('/api/upload', {
          method: 'POST',
          body: formData,
        });

        if (!uploadRes.ok) throw new Error(`Failed to upload ${file.name}`);
      }

      // Start agent pipeline
      await fetch('/api/agents/run', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ projectId: project.id }),
      });

      // Wait a bit to show the animation
      await new Promise(resolve => setTimeout(resolve, 2000));

      // Success!
      addToast(`Project "${projectName}" created successfully!`, 'success');
      onOpenChange(false);
      onProjectCreated?.();
    } catch (error) {
      console.error('Error creating project:', error);
      addToast('Failed to create project. Please try again.', 'error');
      setIsCreating(false);
    }
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-[600px] overflow-hidden">
        <DialogHeader>
          <DialogTitle className="text-2xl font-serif">Create New Project</DialogTitle>
          <DialogDescription>
            Upload research papers and let our multi-agent system analyze them.
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-6 py-4">
          {isCreating ? (
            <div className="space-y-6 animate-fadeIn">
              <div className="text-center space-y-2">
                <div className="relative w-16 h-16 mx-auto">
                  <div className="absolute inset-0 rounded-full border-4 border-muted" />
                  <div className="absolute inset-0 rounded-full border-4 border-primary border-t-transparent animate-spin" />
                  <div className="absolute inset-0 flex items-center justify-center">
                    <span className="text-xs font-bold">{Math.round(((currentStep + 1) / AGENT_STEPS.length) * 100)}%</span>
                  </div>
                </div>
                <h3 className="font-semibold text-lg">Initializing Research Pipeline</h3>
                <p className="text-muted-foreground text-sm">Please wait while we set up your agents...</p>
              </div>

              <div className="grid grid-cols-2 gap-3">
                {AGENT_STEPS.map((step, index) => (
                  <div
                    key={step.id}
                    className={cn(
                      "flex items-center gap-3 p-3 rounded-lg border transition-all duration-500",
                      index <= currentStep
                        ? "bg-primary/5 border-primary/20"
                        : "bg-muted/30 border-transparent opacity-50"
                    )}
                  >
                    <div className={cn(
                      "w-6 h-6 rounded-full flex items-center justify-center text-xs border",
                      index < currentStep
                        ? "bg-primary text-primary-foreground border-primary"
                        : index === currentStep
                          ? "border-primary text-primary animate-pulse"
                          : "border-muted-foreground text-muted-foreground"
                    )}>
                      {index < currentStep ? <CheckCircle2 className="w-3.5 h-3.5" /> : index + 1}
                    </div>
                    <div>
                      <div className="font-medium text-sm">{step.label}</div>
                      <div className="text-xs text-muted-foreground">{step.description}</div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ) : (
            <div className="space-y-4 animate-fadeIn">
              {/* Project Name */}
              <div className="space-y-2">
                <label className="text-sm font-medium">
                  Project Name <span className="text-destructive">*</span>
                </label>
                <input
                  type="text"
                  value={projectName}
                  onChange={(e) => setProjectName(e.target.value)}
                  placeholder="e.g., Quantum Computing Advances 2024"
                  className="w-full px-3 py-2 bg-background border rounded-lg focus:outline-none focus:ring-2 focus:ring-ring transition-all"
                  autoFocus
                />
              </div>

              {/* Description */}
              <div className="space-y-2">
                <label className="text-sm font-medium">
                  Description
                </label>
                <textarea
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                  placeholder="Brief description of your research topic..."
                  rows={2}
                  className="w-full px-3 py-2 bg-background border rounded-lg focus:outline-none focus:ring-2 focus:ring-ring resize-none transition-all"
                />
              </div>

              {/* Drag & Drop File Upload */}
              <div className="space-y-2">
                <label className="text-sm font-medium">
                  Research Papers <span className="text-destructive">*</span>
                </label>
                <div
                  className={cn(
                    "relative border-2 border-dashed rounded-xl p-8 text-center transition-all duration-200 ease-in-out",
                    dragActive
                      ? "border-primary bg-primary/5 scale-[1.01]"
                      : "border-muted-foreground/25 hover:border-primary/50 hover:bg-muted/20"
                  )}
                  onDragEnter={handleDrag}
                  onDragLeave={handleDrag}
                  onDragOver={handleDrag}
                  onDrop={handleDrop}
                  onClick={() => fileInputRef.current?.click()}
                >
                  <input
                    ref={fileInputRef}
                    type="file"
                    multiple
                    accept=".pdf,.doc,.docx"
                    onChange={handleFileChange}
                    className="hidden"
                  />

                  <div className="flex flex-col items-center gap-2 pointer-events-none">
                    <div className="p-3 bg-background rounded-full shadow-sm border">
                      <Upload className="w-6 h-6 text-primary" />
                    </div>
                    <div>
                      <p className="font-medium">Click to upload or drag and drop</p>
                      <p className="text-xs text-muted-foreground mt-1">
                        PDF or DOCX (max 10MB each)
                      </p>
                    </div>
                  </div>
                </div>

                {/* File List */}
                {files.length > 0 && (
                  <div className="grid grid-cols-1 gap-2 mt-3 max-h-[150px] overflow-y-auto pr-2 custom-scrollbar">
                    {files.map((file, index) => (
                      <div key={index} className="flex items-center gap-3 p-2 bg-card border rounded-lg group animate-slideIn">
                        <div className="p-2 bg-primary/10 rounded">
                          <FileType className="w-4 h-4 text-primary" />
                        </div>
                        <div className="flex-1 min-w-0">
                          <p className="text-sm font-medium truncate">{file.name}</p>
                          <p className="text-xs text-muted-foreground">{(file.size / 1024).toFixed(1)} KB</p>
                        </div>
                        <Button
                          variant="ghost"
                          size="icon"
                          className="h-8 w-8 text-muted-foreground hover:text-destructive opacity-0 group-hover:opacity-100 transition-opacity"
                          onClick={(e) => {
                            e.stopPropagation();
                            removeFile(index);
                          }}
                        >
                          <X className="w-4 h-4" />
                        </Button>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>
          )}
        </div>

        <DialogFooter className="sm:justify-between">
          {!isCreating && (
            <>
              <Button variant="ghost" onClick={() => onOpenChange(false)}>
                Cancel
              </Button>
              <Button
                onClick={handleCreate}
                disabled={!projectName.trim() || files.length === 0}
                className="gap-2"
              >
                Create Project
                <CheckCircle2 className="w-4 h-4" />
              </Button>
            </>
          )}
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

'use client';

import React, { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { GripVertical, Plus, Trash2, Edit2, Check, X } from 'lucide-react';

interface OutlineSection {
    title: string;
    description: string;
    subsections?: string[];
    order: number;
}

interface OutlineEditorProps {
    outline: {
        title: string;
        abstract: string;
        sections: OutlineSection[];
    };
    onSave?: (outline: any) => void;
    readOnly?: boolean;
}

export const OutlineEditor: React.FC<OutlineEditorProps> = ({
    outline: initialOutline,
    onSave,
    readOnly = false,
}) => {
    const [outline, setOutline] = useState(initialOutline);
    const [editingSection, setEditingSection] = useState<number | null>(null);
    const [draggedItem, setDraggedItem] = useState<number | null>(null);

    const handleDragStart = (index: number) => {
        setDraggedItem(index);
    };

    const handleDragOver = (e: React.DragEvent, index: number) => {
        e.preventDefault();
        if (draggedItem === null || draggedItem === index) return;

        const newSections = [...outline.sections];
        const draggedSection = newSections[draggedItem];
        newSections.splice(draggedItem, 1);
        newSections.splice(index, 0, draggedSection);

        // Update order
        newSections.forEach((section, idx) => {
            section.order = idx + 1;
        });

        setOutline({ ...outline, sections: newSections });
        setDraggedItem(index);
    };

    const handleDragEnd = () => {
        setDraggedItem(null);
    };

    const handleEditSection = (index: number) => {
        setEditingSection(index);
    };

    const handleSaveSection = (index: number, updates: Partial<OutlineSection>) => {
        const newSections = [...outline.sections];
        newSections[index] = { ...newSections[index], ...updates };
        setOutline({ ...outline, sections: newSections });
        setEditingSection(null);
    };

    const handleDeleteSection = (index: number) => {
        const newSections = outline.sections.filter((_, idx) => idx !== index);
        newSections.forEach((section, idx) => {
            section.order = idx + 1;
        });
        setOutline({ ...outline, sections: newSections });
    };

    const handleAddSection = () => {
        const newSection: OutlineSection = {
            title: 'New Section',
            description: 'Section description',
            subsections: [],
            order: outline.sections.length + 1,
        };
        setOutline({ ...outline, sections: [...outline.sections, newSection] });
        setEditingSection(outline.sections.length);
    };

    return (
        <Card className="animate-fadeIn">
            <CardHeader>
                <div className="flex items-center justify-between">
                    <div>
                        <CardTitle>Research Paper Outline</CardTitle>
                        <CardDescription>
                            {readOnly ? 'View paper structure' : 'Drag to reorder, click to edit'}
                        </CardDescription>
                    </div>
                    {!readOnly && (
                        <div className="flex gap-2">
                            <Button variant="outline" size="sm" onClick={handleAddSection}>
                                <Plus className="w-4 h-4 mr-2" />
                                Add Section
                            </Button>
                            {onSave && (
                                <Button size="sm" onClick={() => onSave(outline)}>
                                    <Check className="w-4 h-4 mr-2" />
                                    Save Changes
                                </Button>
                            )}
                        </div>
                    )}
                </div>
            </CardHeader>
            <CardContent className="space-y-4">
                {/* Title */}
                <div>
                    <label className="text-sm font-semibold mb-1 block">Title</label>
                    <Input
                        value={outline.title}
                        onChange={(e) => setOutline({ ...outline, title: e.target.value })}
                        readOnly={readOnly}
                        className="text-lg font-semibold"
                    />
                </div>

                {/* Abstract */}
                <div>
                    <label className="text-sm font-semibold mb-1 block">Abstract Outline</label>
                    <textarea
                        value={outline.abstract}
                        onChange={(e) => setOutline({ ...outline, abstract: e.target.value })}
                        readOnly={readOnly}
                        className="w-full px-3 py-2 bg-background border rounded-lg text-sm min-h-[80px]"
                    />
                </div>

                {/* Sections */}
                <div>
                    <label className="text-sm font-semibold mb-2 block">Sections</label>
                    <div className="space-y-2">
                        {outline.sections.map((section, index) => (
                            <div
                                key={index}
                                draggable={!readOnly}
                                onDragStart={() => handleDragStart(index)}
                                onDragOver={(e) => handleDragOver(e, index)}
                                onDragEnd={handleDragEnd}
                                className={`border rounded-lg p-4 bg-card ${!readOnly ? 'cursor-move hover:border-primary' : ''
                                    } ${draggedItem === index ? 'opacity-50' : ''}`}
                            >
                                <div className="flex items-start gap-3">
                                    {!readOnly && <GripVertical className="w-5 h-5 text-muted-foreground mt-1" />}

                                    <div className="flex-1">
                                        {editingSection === index ? (
                                            <div className="space-y-2">
                                                <Input
                                                    value={section.title}
                                                    onChange={(e) =>
                                                        handleSaveSection(index, { title: e.target.value })
                                                    }
                                                    className="font-semibold"
                                                    autoFocus
                                                />
                                                <textarea
                                                    value={section.description}
                                                    onChange={(e) =>
                                                        handleSaveSection(index, { description: e.target.value })
                                                    }
                                                    className="w-full px-3 py-2 bg-background border rounded-lg text-sm"
                                                    rows={2}
                                                />
                                                <div className="flex gap-2">
                                                    <Button
                                                        size="sm"
                                                        variant="outline"
                                                        onClick={() => setEditingSection(null)}
                                                    >
                                                        <Check className="w-4 h-4" />
                                                    </Button>
                                                </div>
                                            </div>
                                        ) : (
                                            <>
                                                <div className="flex items-center justify-between mb-1">
                                                    <h4 className="font-semibold">
                                                        {section.order}. {section.title}
                                                    </h4>
                                                    {!readOnly && (
                                                        <div className="flex gap-1">
                                                            <Button
                                                                size="sm"
                                                                variant="ghost"
                                                                onClick={() => handleEditSection(index)}
                                                            >
                                                                <Edit2 className="w-4 h-4" />
                                                            </Button>
                                                            <Button
                                                                size="sm"
                                                                variant="ghost"
                                                                onClick={() => handleDeleteSection(index)}
                                                            >
                                                                <Trash2 className="w-4 h-4" />
                                                            </Button>
                                                        </div>
                                                    )}
                                                </div>
                                                <p className="text-sm text-muted-foreground">
                                                    {section.description}
                                                </p>
                                                {section.subsections && section.subsections.length > 0 && (
                                                    <ul className="mt-2 ml-4 text-sm text-muted-foreground list-disc">
                                                        {section.subsections.map((sub, subIdx) => (
                                                            <li key={subIdx}>{sub}</li>
                                                        ))}
                                                    </ul>
                                                )}
                                            </>
                                        )}
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </CardContent>
        </Card>
    );
};

'use client';

import React from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { MathRenderer } from './math-renderer';
import { Button } from './ui/button';
import { Download, FileText } from 'lucide-react';

interface PaperViewerProps {
    paper: {
        title: string;
        abstract: string;
        fullText?: string;
        introduction?: string;
        literatureReview?: string;
        methodology?: string;
        results?: string;
        discussion?: string;
        conclusion?: string;
        references?: string[];
    };
    onDownload?: () => void;
}

export const PaperViewer: React.FC<PaperViewerProps> = ({ paper, onDownload }) => {
    // Use fullText if available, otherwise compile from sections
    const content = paper.fullText || compileFromSections(paper);

    return (
        <Card className="animate-fadeIn">
            <CardHeader>
                <div className="flex items-center justify-between">
                    <div className="flex items-center gap-3">
                        <FileText className="w-6 h-6 text-primary" />
                        <CardTitle className="text-2xl">{paper.title}</CardTitle>
                    </div>
                    {onDownload && (
                        <Button variant="outline" onClick={onDownload}>
                            <Download className="w-4 h-4 mr-2" />
                            Download PDF
                        </Button>
                    )}
                </div>
            </CardHeader>
            <CardContent className="prose prose-sm max-w-none dark:prose-invert">
                <MathRenderer content={content} className="space-y-6" />
            </CardContent>
        </Card>
    );
};

function compileFromSections(paper: any): string {
    let text = `# ${paper.title}\n\n`;

    if (paper.abstract) {
        text += `## Abstract\n\n${paper.abstract}\n\n`;
    }

    if (paper.introduction) {
        text += `## Introduction\n\n${paper.introduction}\n\n`;
    }

    if (paper.literatureReview) {
        text += `## Literature Review\n\n${paper.literatureReview}\n\n`;
    }

    if (paper.methodology) {
        text += `## Methodology\n\n${paper.methodology}\n\n`;
    }

    if (paper.results) {
        text += `## Results\n\n${paper.results}\n\n`;
    }

    if (paper.discussion) {
        text += `## Discussion\n\n${paper.discussion}\n\n`;
    }

    if (paper.conclusion) {
        text += `## Conclusion\n\n${paper.conclusion}\n\n`;
    }

    if (paper.references && paper.references.length > 0) {
        text += `## References\n\n`;
        paper.references.forEach((ref: string, idx: number) => {
            text += `${idx + 1}. ${ref}\n`;
        });
    }

    return text;
}

'use client';

import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Progress } from '@/components/ui/progress';
import { Badge } from '@/components/ui/badge';
import { Project } from '@/lib/types';
import { FileText, Calendar, TrendingUp, Clock, Tag, MoreHorizontal } from 'lucide-react';
import { Button } from '@/components/ui/button';

interface ProjectCardProps {
    project: Project;
    onClick?: () => void;
}

export function ProjectCard({ project, onClick }: ProjectCardProps) {
    const getStatusVariant = (status: Project['status']) => {
        switch (status) {
            case 'completed':
                return 'default';
            case 'processing':
                return 'secondary';
            case 'error':
                return 'destructive';
            default:
                return 'outline';
        }
    };

    const getStatusText = (status: Project['status']) => {
        switch (status) {
            case 'completed':
                return 'Completed';
            case 'processing':
                return 'Processing';
            case 'error':
                return 'Error';
            default:
                return 'Idle';
        }
    };

    const getProgressColor = (progress: number) => {
        if (progress >= 75) return 'text-primary';
        if (progress >= 50) return 'text-chart-3';
        return 'text-muted-foreground';
    };

    // Mock tags based on project name (in a real app, these would come from the backend)
    const tags = ['AI', 'Research', 'Analysis'];
    if (project.title.toLowerCase().includes('quantum')) tags.push('Quantum');
    if (project.title.toLowerCase().includes('bio')) tags.push('Biology');
    if (project.title.toLowerCase().includes('climate')) tags.push('Climate');

    return (
        <Card
            className="group relative overflow-hidden cursor-pointer transition-all duration-300 hover:shadow-premium-lg hover:-translate-y-1 border-muted/40 bg-card/50 backdrop-blur-sm"
            onClick={onClick}
        >
            {/* Top colored bar to simulate folder tab */}
            <div className="absolute top-0 left-0 right-0 h-1 bg-gradient-to-r from-primary/50 via-secondary/50 to-primary/50 opacity-50 group-hover:opacity-100 transition-opacity" />

            <CardHeader className="pb-3">
                <div className="flex items-start justify-between gap-4">
                    <div className="space-y-1.5">
                        <CardTitle className="text-xl font-serif leading-tight group-hover:text-primary transition-colors">
                            {project.title}
                        </CardTitle>
                        <div className="flex flex-wrap gap-2">
                            {tags.slice(0, 3).map(tag => (
                                <Badge key={tag} variant="secondary" className="text-[10px] h-5 px-1.5 font-normal bg-secondary/50 text-secondary-foreground/80">
                                    {tag}
                                </Badge>
                            ))}
                        </div>
                    </div>
                    <Button variant="ghost" size="icon" className="h-8 w-8 -mr-2 text-muted-foreground opacity-0 group-hover:opacity-100 transition-opacity">
                        <MoreHorizontal className="w-4 h-4" />
                    </Button>
                </div>
            </CardHeader>

            <CardContent>
                <p className="text-sm text-muted-foreground line-clamp-2 mb-6 h-10">
                    {project.description || 'No description provided.'}
                </p>

                <div className="space-y-4">
                    {/* Progress Section */}
                    <div className="space-y-2">
                        <div className="flex items-center justify-between text-xs">
                            <span className="text-muted-foreground flex items-center gap-1.5">
                                {project.status === 'processing' ? (
                                    <Clock className="w-3 h-3 animate-pulse text-primary" />
                                ) : (
                                    <TrendingUp className="w-3 h-3" />
                                )}
                                {getStatusText(project.status)}
                            </span>
                            <span className={`font-mono font-medium ${getProgressColor(project.progress)}`}>
                                {project.progress}%
                            </span>
                        </div>
                        <Progress value={project.progress} className="h-1.5 bg-muted/50" />
                    </div>

                    {/* Footer Metadata */}
                    <div className="flex items-center justify-between pt-4 border-t border-border/50">
                        <div className="flex items-center gap-3 text-xs text-muted-foreground">
                            <div className="flex items-center gap-1.5">
                                <FileText className="w-3.5 h-3.5" />
                                <span>{project.documentCount}</span>
                            </div>
                            <div className="flex items-center gap-1.5">
                                <Calendar className="w-3.5 h-3.5" />
                                <span>{project.lastUpdated.toLocaleDateString(undefined, { month: 'short', day: 'numeric' })}</span>
                            </div>
                        </div>

                        <div className="opacity-0 group-hover:opacity-100 transition-opacity transform translate-x-2 group-hover:translate-x-0 duration-300">
                            <span className="text-xs font-medium text-primary flex items-center gap-1">
                                View Details ΓåÆ
                            </span>
                        </div>
                    </div>
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { AgentPipeline } from '@/components/agent-pipeline';
import { QualityMetricsChart } from '@/components/quality-metrics-chart';
import { CitationChart } from '@/components/citation-chart';
import { WordCountTrend } from '@/components/word-count-trend';
import { ConceptNetwork } from '@/components/concept-network';
import { HypothesisTable } from '@/components/hypothesis-table';
import { TopicClusterHeatmap } from '@/components/topic-cluster-heatmap';
import { OutlineEditor } from '@/components/outline-editor';
import { PaperViewer } from '@/components/paper-viewer';
import { StatisticsViewer } from '@/components/statistics-viewer';
import { ArrowLeft, RefreshCw, Download, FileText, Presentation } from 'lucide-react';
import { generateThemedPPT } from '@/lib/ppt-generator';
import {
    mockQualityMetrics,
    mockCitationData,
    mockWordCountTrend,
} from '@/lib/mock-data';

interface ProjectDetailsViewProps {
    project: any;
    onBack: () => void;
    onRefresh?: () => void;
}

export function ProjectDetailsView({ project, onBack, onRefresh }: ProjectDetailsViewProps) {
    const [activeTab, setActiveTab] = useState('overview');

    if (!project) return null;

    // Get the latest outline and presentation from agentRuns
    const getLatestAgentOutput = (agentName: string) => {
        const run = project.agentRuns?.find(
            (r: any) => r.agentName === agentName && r.status === 'completed' && r.output
        );
        return run ? JSON.parse(run.output) : null;
    };

    const outline = getLatestAgentOutput('outliner');
    const paper = getLatestAgentOutput('writer');
    const presentation = getLatestAgentOutput('presenter');

    const handleDownloadPPT = async () => {
        if (presentation && presentation.slides) {
            generateThemedPPT(presentation.slides, `${project.name}_Presentation`);
        } else {
            // Fetch from API if not in agent runs
            try {
                const response = await fetch(`/api/download/ppt/${project.id}`);
                const data = await response.json();
                if (data.slides) {
                    generateThemedPPT(data.slides, data.title || `${project.name}_Presentation`);
                }
            } catch (error) {
                console.error('Failed to download PPT:', error);
                alert('No presentation available yet. Please wait for the pipeline to complete.');
            }
        }
    };

    return (
        <div className="space-y-8 animate-fadeIn">
            {/* Header */}
            <div className="flex items-center justify-between">
                <div className="flex items-center gap-4">
                    <Button variant="ghost" size="icon" onClick={onBack} className="rounded-full hover:bg-muted">
                        <ArrowLeft className="w-5 h-5" />
                    </Button>
                    <div>
                        <h1 className="text-3xl font-serif font-bold text-foreground">
                            {project.name}
                        </h1>
                        <p className="text-muted-foreground">
                            {project.description || 'Research Project Analysis'}
                        </p>
                    </div>
                </div>
                <div className="flex gap-2">
                    {presentation && (
                        <Button variant="default" size="sm" onClick={handleDownloadPPT} className="gap-2">
                            <Presentation className="w-4 h-4" />
                            Download PPT
                        </Button>
                    )}
                    <Button variant="outline" size="sm" onClick={onRefresh} className="gap-2">
                        <RefreshCw className="w-4 h-4" />
                        Refresh
                    </Button>
                </div>
            </div>

            {/* Pipeline Status */}
            <section className="animate-fadeIn" style={{ animationDelay: '0.1s' }}>
                <h2 className="text-2xl font-serif font-semibold mb-6 flex items-center gap-2">
                    <span className="w-1 h-8 bg-chart-2 rounded-full" />
                    Pipeline Status
                </h2>
                <AgentPipeline
                    status={project.status}
                    progress={project.progress}
                    agentRuns={project.agentRuns}
                />
            </section>

            {/* Tabs for different views */}
            <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
                <TabsList className="grid w-full grid-cols-4">
                    <TabsTrigger value="overview">Overview</TabsTrigger>
                    <TabsTrigger value="outline">Outline</TabsTrigger>
                    <TabsTrigger value="paper">Paper</TabsTrigger>
                    <TabsTrigger value="analytics">Analytics</TabsTrigger>
                    <TabsTrigger value="statistics">Statistics</TabsTrigger>
                </TabsList>

                {/* Overview Tab */}
                <TabsContent value="overview" className="space-y-8">
                    <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                        <section>
                            <h2 className="text-2xl font-serif font-semibold mb-6 flex items-center gap-2">
                                <span className="w-1 h-8 bg-chart-4 rounded-full" />
                                Quality Metrics
                            </h2>
                            <QualityMetricsChart metrics={mockQualityMetrics} />
                        </section>

                        <section>
                            <h2 className="text-2xl font-serif font-semibold mb-6 flex items-center gap-2">
                                <span className="w-1 h-8 bg-chart-5 rounded-full" />
                                Citation Analysis
                            </h2>
                            <CitationChart data={mockCitationData} />
                        </section>
                    </div>

                    <section>
                        <h2 className="text-2xl font-serif font-semibold mb-6 flex items-center gap-2">
                            <span className="w-1 h-8 bg-chart-1 rounded-full" />
                            Word Count Trend
                        </h2>
                        <WordCountTrend data={mockWordCountTrend} />
                    </section>

                    <div className="grid grid-cols-1 xl:grid-cols-2 gap-8">
                        <section>
                            <h2 className="text-2xl font-serif font-semibold mb-6 flex items-center gap-2">
                                <span className="w-1 h-8 bg-chart-3 rounded-full" />
                                Concept Network
                            </h2>
                            <ConceptNetwork nodes={project.conceptNodes || []} />
                        </section>

                        <section>
                            <h2 className="text-2xl font-serif font-semibold mb-6 flex items-center gap-2">
                                <span className="w-1 h-8 bg-chart-2 rounded-full" />
                                Generated Hypotheses
                            </h2>
                            <HypothesisTable hypotheses={project.hypotheses || []} />
                        </section>
                    </div>
                </TabsContent>

                {/* Outline Tab */}
                <TabsContent value="outline">
                    {outline ? (
                        <OutlineEditor outline={outline} readOnly={true} />
                    ) : (
                        <div className="text-center py-12 text-muted-foreground">
                            <FileText className="w-16 h-16 mx-auto mb-4 opacity-50" />
                            <p>Outline will appear here once the outliner agent completes.</p>
                        </div>
                    )}
                </TabsContent>

                {/* Paper Tab */}
                <TabsContent value="paper">
                    {paper ? (
                        <PaperViewer paper={paper} />
                    ) : (
                        <div className="text-center py-12 text-muted-foreground">
                            <FileText className="w-16 h-16 mx-auto mb-4 opacity-50" />
                            <p>Research paper will appear here once the writer agent completes.</p>
                        </div>
                    )}
                </TabsContent>

                {/* Analytics Tab */}
                <TabsContent value="analytics" className="space-y-8">
                    <TopicClusterHeatmap />

                    <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                        <QualityMetricsChart metrics={mockQualityMetrics} />
                        <CitationChart data={mockCitationData} />
                    </div>
                </TabsContent>

                {/* Statistics Tab */}
                <TabsContent value="statistics">
                    <StatisticsViewer statistics={project.statistics || []} />
                </TabsContent>
            </Tabs>
        </div>
    );
}

'use client';

import { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Home, FolderOpen, BarChart3, Settings, Plus, Moon, Sun } from 'lucide-react';
import { cn } from '@/lib/utils';

interface SidebarProps {
    onNewProject?: () => void;
    activeTab?: string;
    onTabChange?: (tab: 'dashboard' | 'projects' | 'analytics' | 'settings') => void;
}

export function Sidebar({ onNewProject, activeTab = 'dashboard', onTabChange }: SidebarProps) {
    const [isDark, setIsDark] = useState(false);
    const [mounted, setMounted] = useState(false);

    // Initialize theme on mount
    useEffect(() => {
        setMounted(true);

        // Check for saved theme preference or default to system preference
        const savedTheme = localStorage.getItem('theme');
        const prefersDark = window.matchMedia('(prefers-color-scheme: dark)').matches;

        const shouldBeDark = savedTheme === 'dark' || (!savedTheme && prefersDark);

        setIsDark(shouldBeDark);
        if (shouldBeDark) {
            document.documentElement.classList.add('dark');
        } else {
            document.documentElement.classList.remove('dark');
        }
    }, []);

    const toggleTheme = () => {
        const newTheme = !isDark;
        setIsDark(newTheme);

        if (newTheme) {
            document.documentElement.classList.add('dark');
            localStorage.setItem('theme', 'dark');
        } else {
            document.documentElement.classList.remove('dark');
            localStorage.setItem('theme', 'light');
        }
    };

    const menuItems = [
        { id: 'dashboard' as const, label: 'Dashboard', icon: Home },
        { id: 'projects' as const, label: 'Projects', icon: FolderOpen },
        { id: 'analytics' as const, label: 'Analytics', icon: BarChart3 },
        { id: 'settings' as const, label: 'Settings', icon: Settings },
    ];

    // Prevent hydration mismatch
    if (!mounted) {
        return (
            <div className="w-64 h-screen bg-sidebar border-r border-sidebar-border flex flex-col animate-pulse">
                <div className="p-6 border-b border-sidebar-border">
                    <div className="h-8 bg-muted rounded w-32 mb-2" />
                    <div className="h-4 bg-muted rounded w-24" />
                </div>
            </div>
        );
    }

    return (
        <div className="w-64 h-screen bg-sidebar border-r border-sidebar-border flex flex-col animate-slideIn">
            {/* Header */}
            <div className="p-6 border-b border-sidebar-border">
                <h1 className="text-2xl font-serif font-bold text-sidebar-foreground">
                    AI Research
                </h1>
                <p className="text-sm text-sidebar-foreground/60 mt-1">Paper Generator</p>
            </div>

            {/* New Project Button */}
            <div className="p-4">
                <Button
                    className="w-full justify-start gap-2 transition-smooth hover:scale-[1.02]"
                    onClick={onNewProject}
                >
                    <Plus className="w-4 h-4" />
                    New Project
                </Button>
            </div>

            {/* Navigation */}
            <nav className="flex-1 px-3 py-2">
                {menuItems.map((item, index) => {
                    const Icon = item.icon;
                    return (
                        <button
                            key={item.id}
                            onClick={() => onTabChange?.(item.id)}
                            className={cn(
                                "w-full flex items-center gap-3 px-3 py-2 rounded-md text-sm font-medium transition-smooth mb-1 animate-slideIn",
                                activeTab === item.id
                                    ? "bg-sidebar-accent text-sidebar-accent-foreground shadow-sm"
                                    : "text-sidebar-foreground hover:bg-sidebar-accent/50 hover:translate-x-1"
                            )}
                            style={{ animationDelay: `${index * 0.05}s` }}
                        >
                            <Icon className="w-4 h-4" />
                            {item.label}
                        </button>
                    );
                })}
            </nav>

            {/* Theme Toggle */}
            <div className="p-4 border-t border-sidebar-border">
                <Button
                    variant="outline"
                    size="sm"
                    onClick={toggleTheme}
                    className="w-full justify-start gap-2 transition-smooth hover:scale-[1.02]"
                >
                    {isDark ? (
                        <>
                            <Sun className="w-4 h-4 animate-scaleIn" />
                            Light Mode
                        </>
                    ) : (
                        <>
                            <Moon className="w-4 h-4 animate-scaleIn" />
                            Dark Mode
                        </>
                    )}
                </Button>

                {/* Theme Indicator */}
                <div className="mt-2 text-xs text-center text-muted-foreground">
                    {isDark ? '≡ƒîÖ Scientific Dark' : 'ΓÿÇ∩╕Å Scientific Light'}
                </div>
            </div>
        </div>
    );
}

'use client';

import React from 'react';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { Badge } from '@/components/ui/badge';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Cell } from 'recharts';

interface Statistic {
    id: string;
    category: string;
    label: string;
    value: number;
    unit?: string | null;
    context: string;
}

interface StatisticsViewerProps {
    statistics: Statistic[];
}

const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884d8', '#82ca9d'];

export function StatisticsViewer({ statistics }: StatisticsViewerProps) {
    if (!statistics || statistics.length === 0) {
        return (
            <Card>
                <CardContent className="pt-6 text-center text-muted-foreground">
                    No statistical data extracted yet. Run the pipeline to analyze documents.
                </CardContent>
            </Card>
        );
    }

    // Group by category
    const groupedStats = statistics.reduce((acc, stat) => {
        if (!acc[stat.category]) {
            acc[stat.category] = [];
        }
        acc[stat.category].push(stat);
        return acc;
    }, {} as Record<string, Statistic[]>);

    // Prepare chart data - filter for items that look comparable (e.g. percentages)
    const percentageStats = statistics.filter(s => s.unit === '%' || s.unit?.toLowerCase().includes('percent'));
    const chartData = percentageStats.slice(0, 10).map(s => ({
        name: s.label.length > 15 ? s.label.substring(0, 15) + '...' : s.label,
        fullLabel: s.label,
        value: s.value,
        category: s.category
    }));

    return (
        <div className="space-y-6 animate-fadeIn">
            {/* Chart Section - only if we have percentage data */}
            {chartData.length > 0 && (
                <Card>
                    <CardHeader>
                        <CardTitle>Key Metrics Comparison</CardTitle>
                        <CardDescription>Visualizing percentage-based statistics found in the research</CardDescription>
                    </CardHeader>
                    <CardContent>
                        <div className="h-[300px] w-full">
                            <ResponsiveContainer width="100%" height="100%">
                                <BarChart data={chartData} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
                                    <CartesianGrid strokeDasharray="3 3" vertical={false} />
                                    <XAxis dataKey="name" />
                                    <YAxis />
                                    <Tooltip
                                        content={({ active, payload }) => {
                                            if (active && payload && payload.length) {
                                                const data = payload[0].payload;
                                                return (
                                                    <div className="bg-popover border rounded-lg p-3 shadow-lg">
                                                        <p className="font-semibold">{data.fullLabel}</p>
                                                        <p className="text-sm">Value: {data.value}%</p>
                                                        <p className="text-xs text-muted-foreground">{data.category}</p>
                                                    </div>
                                                );
                                            }
                                            return null;
                                        }}
                                    />
                                    <Bar dataKey="value" radius={[4, 4, 0, 0]}>
                                        {chartData.map((entry, index) => (
                                            <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                                        ))}
                                    </Bar>
                                </BarChart>
                            </ResponsiveContainer>
                        </div>
                    </CardContent>
                </Card>
            )}

            {/* Detailed Table Section */}
            <Card>
                <CardHeader>
                    <CardTitle>Extracted Statistics</CardTitle>
                    <CardDescription>Detailed list of quantitative findings from the documents</CardDescription>
                </CardHeader>
                <CardContent>
                    <Table>
                        <TableHeader>
                            <TableRow>
                                <TableHead>Category</TableHead>
                                <TableHead>Metric</TableHead>
                                <TableHead className="text-right">Value</TableHead>
                                <TableHead>Context</TableHead>
                            </TableRow>
                        </TableHeader>
                        <TableBody>
                            {statistics.map((stat) => (
                                <TableRow key={stat.id}>
                                    <TableCell>
                                        <Badge variant="outline">{stat.category}</Badge>
                                    </TableCell>
                                    <TableCell className="font-medium">{stat.label}</TableCell>
                                    <TableCell className="text-right font-mono">
                                        {stat.value} {stat.unit}
                                    </TableCell>
                                    <TableCell className="text-muted-foreground text-sm max-w-md truncate" title={stat.context}>
                                        "{stat.context}"
                                    </TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </CardContent>
            </Card>
        </div>
    );
}

