import { geminiClient } from '../gemini-client';

export interface ReviewFeedback {
    section: string;
    score: number; // 0-100
    strengths: string[];
    weaknesses: string[];
    suggestions: string[];
}

export interface ReviewReport {
    overallScore: number;
    summary: string;
    sectionReviews: ReviewFeedback[];
    criticalIssues: string[];
    recommendations: string[];
}

export class ReviewerAgent {
    async process(paperContent: string, outline?: any): Promise<ReviewReport> {
        const prompt = `You are an academic reviewer evaluating this research paper draft:

${paperContent.substring(0, 10000)}

${outline ? `Expected outline structure:\n${JSON.stringify(outline, null, 2)}\n` : ''}

Provide a comprehensive review evaluating:
1. Logical flow and coherence
2. Completeness of each section
3. Quality of arguments and evidence
4. Writing clarity and style
5. Adherence to academic standards

Return as JSON:
{
  "overallScore": 75,
  "summary": "Overall assessment in 2-3 sentences",
  "sectionReviews": [
    {
      "section": "Introduction",
      "score": 80,
      "strengths": ["Clear problem statement", "Well-motivated research"],
      "weaknesses": ["Missing some background context"],
      "suggestions": ["Add more literature context", "Clarify research gap"]
    }
  ],
  "criticalIssues": [
    "Methodology section needs more detail",
    "Results lack statistical significance tests"
  ],
  "recommendations": [
    "Expand the literature review",
    "Add more quantitative results",
    "Improve transition between sections"
  ]
}`;

        try {
            const result = await geminiClient.generateJSON<ReviewReport>(prompt);
            return result;
        } catch (error) {
            console.error('Reviewer Agent Error:', error);
            return {
                overallScore: 70,
                summary: 'Paper shows promise but needs refinement in several areas.',
                sectionReviews: [
                    {
                        section: 'Overall',
                        score: 70,
                        strengths: ['Coherent structure', 'Clear research direction'],
                        weaknesses: ['Some sections need more depth'],
                        suggestions: ['Expand methodology', 'Add more references'],
                    },
                ],
                criticalIssues: ['Review unavailable - using default assessment'],
                recommendations: [
                    'Review each section for completeness',
                    'Ensure all claims are supported by evidence',
                    'Check for logical flow and transitions',
                ],
            };
        }
    }

    /**
     * Quick validation check for paper completeness
     */
    async validateCompleteness(paperContent: string, requiredSections: string[]): Promise<{
        complete: boolean;
        missingSections: string[];
        issues: string[];
    }> {
        const prompt = `Check if this paper contains all required sections: ${requiredSections.join(', ')}

Paper content:
${paperContent.substring(0, 5000)}

Return JSON:
{
  "complete": true/false,
  "missingSections": ["Section Name"],
  "issues": ["Description of any structural issues"]
}`;

        try {
            const result = await geminiClient.generateJSON<{
                complete: boolean;
                missingSections: string[];
                issues: string[];
            }>(prompt);
            return result;
        } catch (error) {
            console.error('Validator Error:', error);
            return {
                complete: false,
                missingSections: [],
                issues: ['Validation check failed'],
            };
        }
    }
}

export const reviewerAgent = new ReviewerAgent();

import { geminiClient } from '../gemini-client';
import { Summary } from './summarizer-agent';
import { Hypothesis } from './hypothesis-agent';
import { Experiment } from './experiment-agent';
import { ResearchOutline, OutlineSection } from './outliner-agent';

export interface ResearchPaper {
    title: string;
    abstract: string;
    introduction: string;
    literatureReview: string;
    methodology: string;
    results: string;
    discussion: string;
    conclusion: string;
    references: string[];
    fullText?: string; // Complete paper in markdown with LaTeX
}

export interface ChapterContent {
    sectionTitle: string;
    content: string;
}

export class WriterAgent {
    /**
     * Legacy single-pass writing (kept for backwards compatibility)
     */
    async process(
        summaries: Summary[],
        hypotheses: Hypothesis[],
        experiments: Experiment[]
    ): Promise<ResearchPaper> {
        const context = `
Summaries: ${summaries.map(s => s.overall).join(' ')}
Top Hypotheses: ${hypotheses.slice(0, 3).map(h => `${h.title}: ${h.description}`).join('; ')}
Experiments: ${experiments.slice(0, 2).map(e => e.methodology).join('; ')}
`;

        const prompt = `Write a comprehensive research paper based on this context:

${context}

IMPORTANT: Use LaTeX notation for any mathematical formulas:
- Inline math: $formula$
- Block math: $$formula$$

Return as JSON:
{
  "title": "Research Paper Title",
  "abstract": "150-200 word abstract",
  "introduction": "Introduction section with background and research questions",
  "literatureReview": "Comprehensive literature review",
  "methodology": "Detailed methodology section",
  "results": "Expected or preliminary results",
  "discussion": "Discussion of implications and findings",
  "conclusion": "Conclusion and future work",
  "references": ["Reference 1", "Reference 2"]
}`;

        try {
            const result = await geminiClient.generateJSON<ResearchPaper>(prompt);
            return result;
        } catch (error) {
            console.error('Writer Agent Error:', error);
            return this.getFallbackPaper();
        }
    }

    /**
     * Enhanced recursive chapter-by-chapter writing
     */
    async processWithOutline(
        outline: ResearchOutline,
        summaries: Summary[],
        hypotheses: Hypothesis[],
        experiments: Experiment[]
    ): Promise<ResearchPaper> {
        const context = {
            summaries: summaries.map(s => s.overall).join('\n\n'),
            hypotheses: hypotheses.map(h => `${h.title}: ${h.description}`).join('\n'),
            experiments: experiments.map(e => `${e.hypothesis}: ${e.methodology}`).join('\n'),
        };

        // Generate abstract first
        const abstract = await this.generateAbstract(outline, context);

        // Generate each chapter recursively
        const chapters: ChapterContent[] = [];
        let previousChapters = '';

        for (const section of outline.sections.sort((a, b) => a.order - b.order)) {
            const chapterContent = await this.generateChapter(
                section,
                context,
                outline,
                previousChapters
            );
            chapters.push(chapterContent);
            previousChapters += `\n\n## ${chapterContent.sectionTitle}\n${chapterContent.content}`;
        }

        // Generate references
        const references = await this.generateReferences(context);

        // Compile full paper
        const fullText = this.compilePaper(outline.title, abstract, chapters, references);

        return {
            title: outline.title,
            abstract,
            introduction: chapters.find(c => c.sectionTitle.toLowerCase().includes('introduction'))?.content || '',
            literatureReview: chapters.find(c => c.sectionTitle.toLowerCase().includes('literature'))?.content || '',
            methodology: chapters.find(c => c.sectionTitle.toLowerCase().includes('methodology'))?.content || '',
            results: chapters.find(c => c.sectionTitle.toLowerCase().includes('result'))?.content || '',
            discussion: chapters.find(c => c.sectionTitle.toLowerCase().includes('discussion'))?.content || '',
            conclusion: chapters.find(c => c.sectionTitle.toLowerCase().includes('conclusion'))?.content || '',
            references,
            fullText,
        };
    }

    private async generateAbstract(outline: ResearchOutline, context: any): Promise<string> {
        const prompt = `Write a 150-200 word academic abstract for this research paper:

Title: ${outline.title}
Abstract should cover: ${outline.abstract}

Research context:
${context.summaries.substring(0, 1000)}

Key hypotheses:
${context.hypotheses.substring(0, 500)}

Write a compelling abstract that summarizes the research problem, approach, and expected contributions.
Use LaTeX for any formulas: inline $formula$ or block $$formula$$`;

        try {
            const result = await geminiClient.generateText(prompt);
            return result;
        } catch (error) {
            console.error('Abstract generation error:', error);
            return 'This paper explores novel approaches in the research domain, leveraging advanced methodologies to address key challenges.';
        }
    }

    private async generateChapter(
        section: OutlineSection,
        context: any,
        outline: ResearchOutline,
        previousChapters: string
    ): Promise<ChapterContent> {
        const prompt = `Write the "${section.title}" section of this research paper:

Paper Title: ${outline.title}
Section Description: ${section.description}
Subsections to cover: ${section.subsections?.join(', ') || 'N/A'}

Research Context:
${context.summaries.substring(0, 2000)}

${previousChapters ? `Previous sections for context:\n${previousChapters.substring(0, 1500)}` : ''}

Guidelines:
- Write 500-800 words for this section
- Use academic writing style
- Include LaTeX notation for formulas: $inline$ or $$block$$
- Reference key findings from the research context
- Ensure logical flow and coherence
- ${section.subsections ? `Cover these subsections: ${section.subsections.join(', ')}` : ''}

Write the complete section content (do NOT include the section title in your response, just the content):`;

        try {
            const content = await geminiClient.generateText(prompt);
            return {
                sectionTitle: section.title,
                content,
            };
        } catch (error) {
            console.error(`Chapter generation error for ${section.title}:`, error);
            return {
                sectionTitle: section.title,
                content: `Content for ${section.title} section is being generated...`,
            };
        }
    }

    private async generateReferences(context: any): Promise<string[]> {
        const prompt = `Based on this research context, suggest 8-10 relevant academic references in proper citation format:

${context.summaries.substring(0, 1500)}

Format each reference in APA style. Return as JSON array of strings.`;

        try {
            const refs = await geminiClient.generateJSON<string[]>(prompt);
            return refs;
        } catch (error) {
            console.error('References generation error:', error);
            return [
                'Smith, J. (2023). Recent Advances in AI Research. Journal of AI Studies, 15(2), 123-145.',
                'Johnson, A., & Lee, B. (2024). Machine Learning Applications. Academic Press.',
            ];
        }
    }

    private compilePaper(
        title: string,
        abstract: string,
        chapters: ChapterContent[],
        references: string[]
    ): string {
        let paper = `# ${title}\n\n`;
        paper += `## Abstract\n\n${abstract}\n\n`;

        for (const chapter of chapters) {
            paper += `## ${chapter.sectionTitle}\n\n${chapter.content}\n\n`;
        }

        paper += `## References\n\n`;
        references.forEach((ref, idx) => {
            paper += `${idx + 1}. ${ref}\n`;
        });

        return paper;
    }

    private getFallbackPaper(): ResearchPaper {
        return {
            title: 'Generated Research Paper',
            abstract: 'Abstract generation in progress...',
            introduction: '',
            literatureReview: '',
            methodology: '',
            results: '',
            discussion: '',
            conclusion: '',
            references: [],
        };
    }
}

export const writerAgent = new WriterAgent();

import PptxGenJS from "pptxgenjs";

export interface SlideData {
    title: string;
    bullets: string[];
    notes?: string;
}

/**
 * Generate a PowerPoint presentation from structured slide data
 * @param slides - Array of slide data objects
 * @param filename - Name for the output file (without extension)
 */
export const generateResearchPPT = (slides: SlideData[], filename: string) => {
    const pres = new PptxGenJS();

    // Set presentation metadata
    pres.author = "AI Research App";
    pres.company = "Research Platform";
    pres.subject = "AI-Generated Research Presentation";
    pres.title = filename;

    slides.forEach((slideContent, index) => {
        const slide = pres.addSlide();

        // Add slide number footer
        slide.addText(`${index + 1} / ${slides.length}`, {
            x: "90%",
            y: "95%",
            w: "10%",
            fontSize: 10,
            color: "999999",
            align: "right",
        });

        // Add Title
        slide.addText(slideContent.title, {
            x: 0.5,
            y: 0.5,
            w: "90%",
            fontSize: 28,
            bold: true,
            color: "363636",
            fontFace: "Arial",
        });

        // Add Bullets
        slide.addText(
            slideContent.bullets.map((b) => `ΓÇó ${b}`).join("\n"),
            {
                x: 0.5,
                y: 1.8,
                w: "90%",
                h: "60%",
                fontSize: 18,
                color: "606060",
                fontFace: "Arial",
                valign: "top",
            }
        );

        // Add Speaker Notes
        if (slideContent.notes) {
            slide.addNotes(slideContent.notes);
        }
    });

    // Write file (browser download)
    pres.writeFile({ fileName: `${filename}.pptx` });
};

/**
 * Generate a presentation with a custom theme
 */
export const generateThemedPPT = (
    slides: SlideData[],
    filename: string,
    theme: {
        primaryColor?: string;
        secondaryColor?: string;
        fontFamily?: string;
    } = {}
) => {
    const pres = new PptxGenJS();

    const primaryColor = theme.primaryColor || "1E40AF";
    const secondaryColor = theme.secondaryColor || "3B82F6";
    const fontFamily = theme.fontFamily || "Arial";

    pres.author = "AI Research App";
    pres.subject = "AI-Generated Research Presentation";
    pres.title = filename;

    slides.forEach((slideContent, index) => {
        const slide = pres.addSlide();

        // Add themed background shape
        slide.addShape(pres.ShapeType.rect, {
            x: 0,
            y: 0,
            w: "100%",
            h: 0.8,
            fill: { color: primaryColor },
        });

        // Add title with white text on colored background
        slide.addText(slideContent.title, {
            x: 0.5,
            y: 0.2,
            w: "90%",
            fontSize: 28,
            bold: true,
            color: "FFFFFF",
            fontFace: fontFamily,
        });

        // Add slide number
        slide.addText(`${index + 1} / ${slides.length}`, {
            x: "90%",
            y: "95%",
            w: "10%",
            fontSize: 10,
            color: "999999",
            align: "right",
        });

        // Add content bullets
        slide.addText(
            slideContent.bullets.map((b) => `ΓÇó ${b}`).join("\n"),
            {
                x: 0.5,
                y: 1.5,
                w: "90%",
                h: "70%",
                fontSize: 18,
                color: "363636",
                fontFace: fontFamily,
                valign: "top",
            }
        );

        // Add speaker notes
        if (slideContent.notes) {
            slide.addNotes(slideContent.notes);
        }
    });

    pres.writeFile({ fileName: `${filename}.pptx` });
};

// Helper function to convert API project to display format
export function convertProject(apiProject: any) {
    return {
        id: apiProject.id,
        title: apiProject.name,
        description: apiProject.description || '',
        status: apiProject.status as 'idle' | 'processing' | 'completed' | 'error',
        lastUpdated: new Date(apiProject.updatedAt),
        documentCount: apiProject._count?.documents || 0,
        progress: apiProject.progress,
    };
}

// Helper to get status badge variant
export function getStatusVariant(status: string) {
    switch (status) {
        case 'completed':
            return 'default' as const;
        case 'processing':
            return 'secondary' as const;
        case 'error':
            return 'destructive' as const;
        default:
            return 'outline' as const;
    }
}
